mapper.insert:
INSERT INTO GE_ITEMS (
    GI_NAME, GCM_CODE, GI_IMG, GI_REMARK, GI_DEL_FLAG
) 
VALUES (
    #{giName}, #{gcmCode}, #{giImg}, #{giRemark}, 'N'
)

mapper.update:
UPDATE GE_ITEMS
SET GI_DEL_FLAG = 'Y'
WHERE GI_CODE =  #{giCode}

UPDATE GE_ITEMS 
SET GI_DEL_FLAG = 'N'
WHERE GI_CODE = #{giCode}
