SELECT b.gcm_name, a.gih_idx, a.gcm_code, a.gi_code, a.gi_name, a.gih_inout, a.gih_qty, a.gih_price, a.gih_remark, TO_CHAR(a.gih_regdate,'YYYY-MM-DD') gih_regdate, a.gih_confirm, (a.gih_qty * a.gih_price) tot_price FROM GE_ITEMS_HIST a JOIN GE_COM_MEMBER b ON a.gcm_code = b.gcm_code WHERE a.gih_qty > 0 AND a.gi_name = #{gi_name}
AND gih_regdate BETWEEN #{start} AND #{end}
ORDER BY b.gcm_name, a.gih_price

SELECT b.gcm_name, a.gih_idx, a.gcm_code, a.gi_code, a.gi_name, a.gih_inout, a.gih_qty, a.gih_price, a.gih_remark, TO_CHAR(a.gih_regdate,'YYYY-MM-DD') gih_regdate, a.gih_confirm, (a.gih_qty * a.gih_price) tot_price FROM GE_ITEMS_HIST a JOIN GE_COM_MEMBER b ON a.gcm_code = b.gcm_code WHERE a.gih_qty > 0 AND a.gi_name = #{gi_name}
AND gih_regdate BETWEEN #{start} AND #{end}
ORDER BY b.gcm_name, a.gih_price LIMIT ${nowPage},10

